import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "./Header";

const Login = ({ isLoggedIn, setIsLoggedIn, setUserName }) => {
  const navigate = useNavigate();
  const [loginData, setLoginData] = useState({ Email: "", Password: "" });

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/user/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(loginData),
        credentials: "include",
      });

      const result = await response.json();
      if (response.ok) {
        alert("Login successful!");
        setIsLoggedIn(true);
        setUserName(result.user.name);
        navigate("/profile");
      } else {
        alert(result.error || "Login failed!");
      }
    } catch (error) {
      console.error("Error during login:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <>
      <Header isLoggedIn={isLoggedIn} userName={""} handleLogout={() => {}} />
      <div style={{ maxWidth: "400px", margin: "2rem auto", textAlign: "center" }}>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="Email"
            placeholder="Email"
            value={loginData.Email}
            onChange={handleChange}
            style={{ display: "block", margin: "1rem auto", padding: "0.5rem", width: "100%" }}
            required
          />
          <input
            type="password"
            name="Password"
            placeholder="Password"
            value={loginData.Password}
            onChange={handleChange}
            style={{ display: "block", margin: "1rem auto", padding: "0.5rem", width: "100%" }}
            required
          />
          <button type="submit" style={{ padding: "0.5rem 1rem", background: "blue", color: "#fff", border: "none", cursor: "pointer" }}>
            Login
          </button>
        </form>
      </div>
    </>
  );
};

export default Login;
