import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Header = ({ isLoggedIn, userName, handleLogout }) => {
  const navigate = useNavigate();

  const handleLogoutClick = () => {
    handleLogout();
    navigate("/login"); // Navigate to login after logout
  };

  return (
    <header style={{ padding: "1rem", background: "#333", color: "#fff", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
      <h1>MyApp</h1>
      <nav>
        {isLoggedIn ? (
          <>
            <span style={{ marginRight: "1rem" }}>Welcome, {userName}!</span>
            <button onClick={handleLogoutClick} style={{ padding: "0.5rem 1rem", background: "red", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}>
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" style={{ color: "#fff", marginRight: "1rem" }}>
              Login
            </Link>
            <Link to="/register" style={{ color: "#fff" }}>
              Register
            </Link>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
