/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import { FaUser, FaLock, FaEnvelope } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

const Login = ({ handleLogin }) => {
  const navigate = useNavigate(); // Hook for navigation
  const [loginData, setLoginData] = useState({ Email: "", Password: "" }); // State to manage form data

  // Handle change in input fields
  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/user/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(loginData),
      });
      const result = await response.json(); // Parse the response

      // Check if the response is OK (login successful)
      if (response.ok) {
        alert("Login successful!");
        handleLogin(result.user.Name); // Pass the username to handleLogin function
        navigate("/"); // Navigate to home page
      } else {
        alert(result.error || "Login failed!"); // Handle failed login
      }
    } catch (error) {
      console.error("Error during login:", error);
      alert("An error occurred. Please try again."); // Handle any errors during the fetch
    }
  };

  return (
    <div className="login-form" style={{ width: "300px", margin: "auto" }}>
      <form onSubmit={handleSubmit}>
        <h1 style={{ textAlign: "center", marginBottom: "1rem" }}>Login</h1>

        {/* Email Input Field */}
        <div className="input-box" style={{ marginBottom: "1rem" }}>
          <input
            type="email"
            name="Email"
            placeholder="Email"
            value={loginData.Email}
            onChange={handleChange}
            required
            style={{
              width: "100%",
              padding: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
              marginBottom: "0.5rem",
            }}
          />
          <FaEnvelope className="icon" style={{ position: "absolute", top: "10px", left: "10px" }} />
        </div>

        {/* Password Input Field */}
        <div className="input-box" style={{ marginBottom: "1rem", position: "relative" }}>
          <input
            type="password"
            name="Password"
            placeholder="Password"
            value={loginData.Password}
            onChange={handleChange}
            required
            style={{
              width: "100%",
              padding: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
              marginBottom: "0.5rem",
            }}
          />
          <FaLock className="icon" style={{ position: "absolute", top: "10px", left: "10px" }} />
        </div>

        {/* Login Button */}
        <button
          type="submit"
          style={{
            width: "100%",
            padding: "0.75rem",
            borderRadius: "4px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            fontSize: "1rem",
            cursor: "pointer",
          }}
        >
          Login
        </button>

        {/* Link to SignUp Page */}
        <div style={{ textAlign: "center", marginTop: "1rem" }}>
          <span>Don't have an account? </span>
          <a
            href="/user/signup"
            style={{
              color: "#4CAF50",
              textDecoration: "none",
              fontWeight: "bold",
            }}
          >
            Sign up
          </a>
        </div>
      </form>
    </div>
  );
};

export default Login;
