import React from 'react';

const Header = ({ isLoggedIn, userName, handleLogout }) => {
  const navStyle = {
    display: 'flex',
    gap: '2rem',
  };

  return (
    <header
      style={{
        backgroundColor: '#1E201E',
        color: 'white',
        padding: '1rem',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}
    >
      <div className="logo" style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>
        THOUGHTLY
      </div>
      <nav style={navStyle}>
        <a href="/" style={{ color: 'white', textDecoration: 'none' }}>
          Home
        </a>
        <a href="/about" style={{ color: 'white', textDecoration: 'none' }}>
          About Us
        </a>
        {!isLoggedIn ? (
          <>
            <a
              href="/login"
              style={{ color: 'white', textDecoration: 'none' }}
            >
              Login
            </a>
            <a
              href="/signup"
              style={{ color: 'white', textDecoration: 'none' }}
            >
              Create Account
            </a>
          </>
        ) : (
          <>
            <a
              href="/blog/add"
              style={{ color: 'white', textDecoration: 'none' }}
            >
              Add Blog
            </a>
            <span style={{ color: 'white', fontWeight: 'bold' }}>{userName}</span>
            <a
              onClick={handleLogout}
              style={{
                color: 'white',
                textDecoration: 'none',
                cursor: 'pointer',
              }}
            >
              Logout
            </a>
          </>
        )}
        <a
          href="/contact"
          style={{ color: 'white', textDecoration: 'none' }}
        >
          Contact Us
        </a>
      </nav>
    </header>
  );
};

export default Header;
