import React, { useEffect, useState } from 'react';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');

  useEffect(() => {
    // Check if the user is authenticated by hitting the profile route
    const checkAuthentication = async () => {
      try {
        const response = await fetch('/user/profile', {
          method: 'GET',
          credentials: 'include', // Send cookies (JWT token)
        });

        if (response.status === 200) {
          const data = await response.json(); // Return user data if authenticated
          setIsLoggedIn(true);
          setUserName(data.user.name); // Set user name after successful authentication
        } else {
          throw new Error('Not authenticated');
        }
      } catch (error) {
        setIsLoggedIn(false); // If not authenticated, update state
        console.error(error);
      }
    };

    checkAuthentication();
  }, []); // Runs once when component is mounted

  const handleLogout = async () => {
    try {
      await fetch('/user/logout', {
        method: 'GET',
        credentials: 'include', // Include cookies to clear JWT token
      });
      setIsLoggedIn(false); // Update state
      setUserName(''); // Clear user data
      window.location.href = '/'; // Redirect to homepage (or login page)
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  const navStyle = {
    display: 'flex',
    gap: '2rem',
  };

  return (
    <header style={{ backgroundColor: '#1E201E', color: 'white', padding: '1rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <div className="watermark"></div>
      <div className="logo" style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>THOUGHTLY</div>

      <nav style={navStyle}>
        <a href="#home" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Home</a>
        <a href="#about" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>About Us</a>

        {!isLoggedIn ? (
          <>
            <a href="/user/login" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Login</a>
            <a href="/user/signup" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Create Account</a>
          </>
        ) : (
          <>
            <a href="/blog/add" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Add Blog</a>
            <span style={{ color: 'white', fontWeight: 'bold' }}>{userName}</span>
            <a onClick={handleLogout} style={{ color: 'white', textDecoration: 'none', cursor: 'pointer', transition: 'color 0.3s' }}>Logout</a>
          </>
        )}

        <a href="#contact" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Contact Us</a>
      </nav>
    </header>
  );
}

export default Header;
