/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from 'react';
import './LoginRegister.css';
import { FaUser, FaLock, FaEnvelope } from "react-icons/fa";
import { useNavigate } from 'react-router-dom';
import Header from './Header'; // Import the Header component

const Login = () => {
  const navigate = useNavigate(); // Initialize the useNavigate hook
  const [action, setAction] = useState('');
  const [loginData, setLoginData] = useState({ Email: '', Password: '' });
  const [registerData, setRegisterData] = useState({ Name: '', Email: '', Password: '' });
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login state
  const [userName, setUserName] = useState(''); // Track the user's name

  const registerLink = () => {
    setAction('active');
  };

  const loginLink = () => {
    setAction('');
  };

  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleRegisterChange = (e) => {
    setRegisterData({ ...registerData, [e.target.name]: e.target.value });
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/user/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData),
      });
      const result = await response.json();
      if (response.ok) {
        alert('Login successful!');
        console.log(result);
        setIsLoggedIn(true); // Set login state to true
        setUserName(result.user.Name); // Set user name after successful login
        navigate('/'); // Navigate to the home page after successful login
      } else {
        alert(result.error || 'Login failed!');
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('An error occurred. Please try again.');
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/user/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(registerData),
      });
      const result = await response.json();
      if (response.ok) {
        alert('Registration successful!');
        console.log(result);
        loginLink(); // Redirect to login form after successful registration
      } else {
        alert(result.error || 'Registration failed!');
      }
    } catch (error) {
      console.error('Error during registration:', error);
      alert('An error occurred. Please try again.');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false); // Reset login state
    setUserName(''); // Reset user name
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <div>
      <Header isLoggedIn={isLoggedIn} userName={userName} handleLogout={handleLogout} /> {/* Pass login state and userName to Header */}

      <div className={`wrapper ${action}`}>
        <div className="form-box login">
          <form onSubmit={handleLoginSubmit}>
            <h1>Login</h1>
            <div className="input-box">
              <input
                type="email"
                name="Email"
                placeholder="Email"
                value={loginData.Email}
                onChange={handleLoginChange}
                required
              />
              <FaEnvelope className="icon" />
            </div>
            <div className="input-box">
              <input
                type="password"
                name="Password"
                placeholder="Password"
                value={loginData.Password}
                onChange={handleLoginChange}
                required
              />
              <FaLock className="icon" />
            </div>
            <div className="remember-forget">
              <label><input type="checkbox" />Remember me</label>
              <a href="#">Forget password?</a>
            </div>
            <button type="submit">Login</button>
            <div className="register-link">
              <p>Don't have an account? <a href="#" onClick={registerLink}>Register</a></p>
            </div>
          </form>
        </div>

        <div className="form-box register">
          <form onSubmit={handleRegisterSubmit}>
            <h1>Registration</h1>
            <div className="input-box">
              <input
                type="text"
                name="Name"
                placeholder="Username"
                value={registerData.Name}
                onChange={handleRegisterChange}
                required
              />
              <FaUser className="icon" />
            </div>
            <div className="input-box">
              <input
                type="email"
                name="Email"
                placeholder="Email"
                value={registerData.Email}
                onChange={handleRegisterChange}
                required
              />
              <FaEnvelope className="icon" />
            </div>
            <div className="input-box">
              <input
                type="password"
                name="Password"
                placeholder="Password"
                value={registerData.Password}
                onChange={handleRegisterChange}
                required
              />
              <FaLock className="icon" />
            </div>
            <div className="remember-forget">
              <label><input type="checkbox" />I agree to terms & conditions</label>
            </div>
            <button type="submit">Register</button>
            <div className="register-link">
              <p>Already have an account? <a href="#" onClick={loginLink}>Login</a></p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
