import React, { useEffect, useState } from 'react';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');

  useEffect(() => {
    // Check if token exists in cookies by making an API request
    const token = document.cookie.split('; ').find(row => row.startsWith('token='));

    if (token) {
      // If token exists in cookies, verify it by calling an API to get user profile
      fetch('/user/profile', {
        method: 'GET',
        credentials: 'include', // Ensure cookies are sent with the request
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.user) {
            setIsLoggedIn(true);
            setUserName(data.user.name); // Set user name from the response
          } else {
            setIsLoggedIn(false);
          }
        })
        .catch(() => {
          setIsLoggedIn(false);
        });
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  const navStyle = {
    display: 'flex',
    gap: '2rem',
  };

  return (
    <header style={{ backgroundColor: '#1E201E', color: 'white', padding: '1rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <div className="watermark"></div>
      <div className="logo" style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>THOUGHTLY</div>

      <nav style={navStyle}>
        <a href="#home" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Home</a>
        <a href="#about" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>About Us</a>

        {!isLoggedIn ? (
          <>
            <a href="/user/login" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Login</a>
            <a href="/user/signup" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Create Account</a>
          </>
        ) : (
          <>
            <a href="/blog/add" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Add Blog</a>
            <span style={{ color: 'white', fontWeight: 'bold' }}>{userName}</span>
            <a href="/user/logout" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Logout</a>
          </>
        )}

        <a href="#contact" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s' }}>Contact Us</a>
      </nav>
    </header>
  );
}

export default Header;
