import React, { useState, useEffect } from 'react';

const UserProfile = () => {
  const [user, setUser] = useState(null); // To store user profile data
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await fetch('http://localhost:5000/user/profile', {
          method: 'GET',
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user profile');
        }

        const data = await response.json();
        setUser(data.user); // Set user data
      } catch (err) {
        setError(err.message || 'An error occurred while fetching profile data');
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserProfile();
  }, []); // Fetch profile on component mount

  if (isLoading) {
    return <div>Loading profile...</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return (
    <div style={{ maxWidth: '600px', margin: '50px auto', textAlign: 'center', padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
      <h1>User Profile</h1>
      {user && (
        <div>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>User ID:</strong> {user.id}</p>
          {/* Add more fields as needed */}
        </div>
      )}
      <button
        onClick={() => alert('Edit functionality coming soon!')}
        style={{
          marginTop: '20px',
          padding: '10px 15px',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          cursor: 'pointer',
          borderRadius: '5px',
        }}
      >
        Edit Profile
      </button>
    </div>
  );
};

export default UserProfile;
