import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Header = ({ isLoggedIn, userName, handleLogout }) => {
  const navigate = useNavigate();

  const handleLogoutClick = () => {
    handleLogout();
    navigate("/login"); // Redirect to login page after logout
  };

  return (
    <header style={{ padding: "1rem", background: "#333", color: "#fff", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
      <h1>Thoughtly</h1>
      <nav>
        {!isLoggedIn ? (
          <>
            <Link to="/login" style={{ color: "#fff", marginRight: "1rem" }}>
              Login
            </Link>
            <Link to="/register" style={{ color: "#fff" }}>
              Register
            </Link>
          </>
        ) : (
          <>
            <Link to="/profile" style={{ color: "#fff", marginRight: "1rem" }}>
              Profile
            </Link>
            <span style={{ marginRight: "1rem" }}>Welcome, {userName}!</span>
            <button
              onClick={handleLogoutClick}
              style={{
                padding: "0.5rem 1rem",
                background: "red",
                color: "#fff",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Logout
            </button>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
